from django.http import HttpResponse
from .models import Producto  
from .carrito import Carrito  
from .models import Producto
from django.shortcuts import render, redirect, get_object_or_404
from decimal import Decimal

def tienda(request):
    productos = Producto.objects.all()
    total_carrito = sum(item['acumulado'] * item['precio'] for item in request.session.get('carrito', {}).values())
    return render(request, 'tienda.html', {'productos': productos, 'total_carrito': total_carrito})

def agregar_producto(request, producto_id):
    carrito = request.session.get('carrito', {})
    producto = get_object_or_404(Producto, id=producto_id)
    if str(producto_id) not in carrito:
        carrito[producto_id] = {
            'nombre': producto.nombre,
            'precio': float(producto.precio),  # Convertir Decimal a float
            'acumulado': 1
        }
    else:
        carrito[producto_id]['acumulado'] += 1
    request.session['carrito'] = carrito
    return redirect('tienda')

def eliminar_producto(request, producto_id):
    carrito = request.session.get('carrito', {})
    if str(producto_id) in carrito:
        if carrito[producto_id]['acumulado'] > 1:
            carrito[producto_id]['acumulado'] -= 1
        else:
            del carrito[producto_id]
        request.session['carrito'] = carrito
    return redirect('tienda')

def limpiar_carrito(request):
    request.session['carrito'] = {}
    return redirect('tienda')





